import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "../assets/css/Products.scss";

const Products = (props) => {
  const { id_cate } = useParams();
  const url = "http://localhost:4000";
  const [products, setProducts] = useState([]);

  useEffect(() => {
    let products = fetch(`${url}/categories/${id_cate}`)
      .then((res) => res.json())
      .then((data) => setProducts(data));
    console.log(products);
  }, [id_cate]);

  return (
    <div className="products">
      <div className="products-container">
        {products.map((product, i) => (
          <a href="#" className="products-card" key={i}>
            <div className="images">
              <img className="img1" src={product.img[0]} alt={product.name} />
              <img className="img2" src={product.img[1]} alt={product.name} />
            </div>
            <div>
              <p>{product.name}</p>
            </div>
            <div>
              <b>{product.price} USD</b>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
};

export default Products;
