import React from "react";
import "../assets/css/ChangePassword.scss";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { loginSuccess } from "../features/auth/authSlice";
import { useFormik } from "formik";
import { LoginSchema } from "../features/validations/UserSchema";

const url = "http://localhost:4000";

const ChangePassword = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const onSubmit = async (values, actions) => {
    console.log(values);
    console.log(actions);

    const credentials = {
      currentPassword: values.currentPassword,
      newPassword: values.password
    };
    console.log(credentials);

    fetch(`${url}/users/update/${userId}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(credentials)
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("PUT request successful:", data);
        dispatch(
          loginSuccess({
            user: data.userInfo,
            token: data.token,
            expiresIn: data.expiresIn
          })
        );
        localStorage.setItem("user", data.userInfo.name);
        localStorage.setItem("token", data.token);
        localStorage.setItem("isAuthenticated", true);
        window.location.replace("/");
      })
      .catch((error, req) => {
        console.error("Error making POST request:", error, req.body);
      });
    actions.resetForm();
  };

  const formik = useFormik({
    initialValues: {
      currentPassword: "",
      password: ""
    },
    validationSchema: LoginSchema,
    onSubmit
  });

  return (
    <div className="login-container">
      <div className="login">
        <h3>LOGIN</h3>
        <form onSubmit={formik.handleSubmit} autoComplete="off">
          <label for="username">Current password *</label>
          <input
            type="text"
            name="email"
            value={formik.values.currentPassword}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
          />
          <label for="password">New password *</label>
          <input
            type="password"
            name="password"
            value={formik.values.password}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            className={
              formik.errors.password && formik.touched.password
                ? "input-error"
                : ""
            }
          />
          {formik.errors.password && formik.touched.password && (
            <p className="error">{formik.errors.password}</p>
          )}
          <input type="checkbox" />
          <span>Remember me</span>
          <button type="submit">LOG IN</button>
        </form>
      </div>
    </div>
  );
};

export default ChangePassword;
