import React from "react";
import { useState, useEffect } from "react";
import "../assets/css/ProductList.scss";

const ProductList = () => {
  const url = "http://localhost:4000";
  const [productList, setProductList] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch(`${url}/products`)
      .then((res) => res.json())
      .then((data) => setProductList(data));
    fetch(`${url}/categories`)
      .then((res) => res.json())
      .then((data) => setCategories(data));
  }, []);


    const handleDelete = async (productId) => {
      const userConfirmed = window.confirm(
        "Bạn có chắc chắn muốn xoá sản phẩm này?"
      );
      if (userConfirmed) {
        try {
          await fetch(url, {
            method: "DELETE", 
            headers: {
              "Content-type": "application/json; charset=UTF-8" 
            }
          })
            .then((response) => response.json())
            .then((data) => console.log(data)) // Manipulate the data retrieved back, if we want to do something with it
            .catch((err) => console.log(err)); // Do something with the error;

          const updatedProducts = products.filter(
            (product) => product._id !== productId
          );
          setProductList(updatedProducts);

          console.log("Sản phẩm đã được xoá");
        } catch (error) {
          console.error("Lỗi khi xoá sản phẩm:", error);
        }
      }
    };

  return (
    <div className="productlist-container">
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th>Category</th>
            <th>Image</th>
            <th>
              <span>Name</span>
            </th>
            <th>Date</th>
            <th>
              <span>Price</span>
            </th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody className="table-group-divider">
          {productList.map((product, index) => {
            const category = categories.filter((c) => c.id === product.id_cate);
            return (
              <tr key={product.id}>
                <td>{index + 1}</td>
                <td>{category[0]?.name.toUpperCase()}</td>
                <td>
                  <img src={product.img[0]} alt={product.name} />
                </td>
                <td>{product.name}</td>
                <td>{new Date(product.date).toLocaleDateString("vi")}</td>
                <td>{product.price} USD</td>
                <td className="action-btn">
                  <a href="#/" className="btn edit">
                    <i class="bx bx-edit-alt"></i>
                  </a>
                  &nbsp;
                  <a href="#/" className="btn del">
                    <i class="bx bxs-trash"></i>
                  </a>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default ProductList;
